import React, { Component } from 'react'
import {
    Button,
    Checkbox,
    Form,
    Input,
    Radio,
    Select,
    TextArea,
} from 'semantic-ui-react'

const options = [
    { key: 'm', text: 'Male', value: 'male' },
    { key: 'f', text: 'Female', value: 'female' },
]

class FormPerso extends Component {
    state = {}

    handleChange = (e, { value }) => this.setState({ value })

    render() {
        const { value } = this.state
        return (
            <Form>
                <Form.Group>
                    <Form.Field
                        width={4}
                        control={Input}
                        label='Name'
                        placeholder='Name'
                    />
                    <Form.Field
                        width={4}
                        control={Select}
                        label='Gender'
                        options={options}
                        placeholder='Gender'
                    />
                </Form.Group>
                <Form.Field
                    width={8}
                    control={Select}
                    label='Race'
                    options={options}
                    placeholder='Race'
                />
                <Form.Group inline>
                    <label>Quantity</label>
                    <Form.Field
                        control={Radio}
                        label='One'
                        value='1'
                        checked={value === '1'}
                        onChange={this.handleChange}
                    />
                    <Form.Field
                        control={Radio}
                        label='Two'
                        value='2'
                        checked={value === '2'}
                        onChange={this.handleChange}
                    />
                    <Form.Field
                        control={Radio}
                        label='Three'
                        value='3'
                        checked={value === '3'}
                        onChange={this.handleChange}
                    />
                </Form.Group>
                <Form.Field
                    control={TextArea}
                    label='About'
                    placeholder='Tell us more about you...'
                />
                <Form.Field
                    control={Checkbox}
                    label='I agree to the Terms and Conditions'
                />
                <Form.Field control={Button}>Submit</Form.Field>
            </Form>
        )
    }
}

export default FormPerso
